import { Component } from '@angular/core';

@Component({
  selector: 'app-assignment',
  templateUrl: './assignment.component.html',
  styleUrls: ['./assignment.component.scss']
})

 
interface Assignment {
  title: String;
  description: String;
  completed: boolean;
}

export class AssignmentComponent {
  
  fetchAssignment(token:String) : any {
    let response = fetch(`http://localhost:8080/api/getAssignments/${token}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      }
    });
    response.then((res) => {
      return res.json().then((data) => {
        return data;
      });
    });
  }
  fetchToken(username:String, password:String):String {
    let response = fetch("http://localhost:8080/api/getMoodleToken", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      }
    });
    response.then((res) => {
      return res.json().then((data) => {
        return data.token.toString();
      });
    });
    return "no token found";
  }
  getAllAssignments() {
    let div = document.getElementById("assignmentDiv");
    let token: String = this.fetchToken("username", "password");
    let assignments = this.fetchAssignment("token");
    div!.innerHTML = assignments.map((assignment: any) => `<tr><th>${assignment.title}</th><th>${assignment.description}</th><th><input type="checkbox" id="checkbox" ${assignment.completed ? "checked":""} ></th></tr>`).join(""); //<th><button id="deletebutton" onclick="deleteTodo(${todo.id})">DELETE</button></th>
    
  }
}
